import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { SET_ACTIVE } from '../../redux/chatReducer';
import { RootState } from '../../redux/store'
import { SET_USER } from '../../redux/userReducer';
import chatServices from '../../services/chatServices';
import userServices from '../../services/userServices';

export default function Participants() {
  const selected=useSelector((state:RootState)=>state.chats.active);
  const user=useSelector((state:RootState)=>state.user.user);
  const chat=useSelector((state:RootState)=>state.user.user?.chats[selected]);
  const dispatch=useDispatch();


  const handleDelete=async()=>{
    try{
      if(chat&&user){
        await chatServices.deleteChat(chat._id);
        await dispatch(SET_ACTIVE(-1));
        user.chats=await chatServices.getAllChats();
        dispatch(SET_USER(user));
        console.log(user);
      }
    }
    catch(e:any){
      if(e.response){
        const msg=e.response.data.msg;
        alert(msg);
      }
    }
  }

  return (
    <div className='chat-participants message-box'>
      <h1>Participants</h1>
      <div className='chat-participants-members'>
        {chat?.users.map(user=><ParticipantInfo key={user._id} user={user}/>)}
      </div>
      <h3 className="chat-participants-delete" onClick={handleDelete}>Leave chat</h3>
    </div>
  )
}

function ParticipantInfo({user}:{user:{_id:string,fullName:string,email:string}}){
  return(
    <div className='participant'>
      <img className="home-user-img" alt="no" src="data:image/png;base64,"/>
      <h2 style={{marginLeft:"0.5rem"}}>{user.fullName}</h2>
    </div>
  )
}
